import React from 'react';
import './Input.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import ReturnQuote from './Quote';

// Firebase Auth for phone
import { collection, getDocs } from "firebase/firestore";
import { signInWithPhoneNumber, RecaptchaVerifier, onAuthStateChanged } from "firebase/auth";
import { auth, db } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';
import BarLoader from 'react-spinners/BarLoader';


export let phoneX = '';

// Input Field form
export default function REturnInPutConnexIon() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='form-input-sign'>
  <FormDataInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-sign-sm'>
  <FormDataInput />
 </div>
);
export const FormDataInput = () => {

 let regular = /[a-z]+/;
 let pushClient = new Array();
 let pushAgent = new Array();

 const [checked, setChecked] = React.useState(true);
 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');
 const [loading, setLoading] = React.useState(false);

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 let isExistClient;
 let isExistAgent;

 const navigation = useNavigate();
 const generateRecaptcha = () => {
  window.recaptchaVerifier = new RecaptchaVerifier('recaptcha-container', {
   'size': 'normal',
   'callback': (response) => {
   },
   'expired-callback': () => {
   }
  }, auth);
 }

 const { register, handleSubmit, formState, reset } = useForm({
  defaultValues: { phone: '+243' }, mode: 'onChange'
 });
 const { isSubmitting, isValid } = formState;

 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {

   setLoading(true);
   setTimeout(() => {
    setLoading(false)
    setOpen(true);
   }, 8000);

   reset();

  } else {

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    pushClient.push(doc.id);
   });

   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    pushAgent.push(doc.id);
   });

   isExistClient = pushClient.some(value => value == data.phone);
   isExistAgent = pushAgent.some(value => value == data.phone);

   if (isExistClient || isExistAgent) {

    let phoneNumber = data.phone;
    phoneX = data.phone;
    generateRecaptcha();
    const appVerifier = window.recaptchaVerifier;

    setLoading(true);
    setTimeout(() => {

     setLoading(false)
     signInWithPhoneNumber(auth, phoneNumber, appVerifier)
      .then((confirmationResult) => {

       // SMS sent. Prompt user to type the code from the message, then sign the
       // user in with confirmationResult.confirm(code).
       window.confirmationResult = confirmationResult;
       phoneX = data.phone;

       setChecked(false);
       setOpen(false);
       reset();

      }).catch((error) => {
       // Error; SMS not sent
       window.console.log(error);
      });

    }, 4000);

   } else {

    setLoading(true);
    setTimeout(() => {
     setLoading(false)
     setOpen(true);
    }, 8000);

    reset();
   }
  }
 };

 return (
  <>
   {loading && <div className='App-loading'></div>}
   {loading && <div className='App-Icon'>
    <BarLoader
     size={15}
     color={'#cfb53b'}
     loading={loading}
    />
   </div>}


   {checked
    ?
    <form onSubmit={handleSubmit(onSubmit)}>
     <ReturnQuote
      Text={`Envoyer, échanger ou accepter des fiat avec un numero personnel sur votre compte.`}
     />
     <input type='phone' id='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />

     <Dialog
      fullWidth={fullWidth}
      maxWidth={maxWidth}
      open={open}
      onClose={handleClose}
     >
      <DialogTitle>MuunganoMoney</DialogTitle>
      <DialogContent>

       <DialogContentText>
        Le numéro de téléphone saisi n'est pas encore enregistré auprès de MuunganoMoney.Ou invalide
     </DialogContentText>

      </DialogContent>
      <DialogActions>
       <Button onClick={handleClose}>Fermer</Button>
      </DialogActions>
     </Dialog>

     <div id='recaptcha-container'></div>
     <button className='Btn-sm'>Connexion</button>
    </form>

    :
    <InputCodeRecaptcha />
   }
  </>
 );
};

// input Recaptcha  verifier!
export const InputCodeRecaptcha = () => {

 const navigation = useNavigate();
 const { register, handleSubmit, formState } = useForm();
 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 const onSubmitOTP = async (data) => {

  let confirmationResult = window.confirmationResult;
  confirmationResult.confirm(data.code).then((result) => {
   // User signed in successfully.
   const user = result.user;
   !!user.uid && navigation('/dash');
  }).catch((error) => {
   // User couldn't sign in (bad verification code?)
   window.console.log(error);
   setOpen(true);
  });
 };

 return (
  <form onSubmit={handleSubmit(onSubmitOTP)}>

   <ReturnQuote Text={'Valider code envoyer.'} />
   <input type='text' placeholder='Code Envoyer' name='code' {...register('code', { required: true })} />
   <button className='Btn-sm'>Connexion</button>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Code incorrect, veuillez vérifier
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 );
};
