import React from 'react';
import { useForm } from 'react-hook-form';
import './Input.css';

export let phoneX = '';
let regular = /[a-z]+/;

export default function ReturnInput() {

 const { register, handleSubmit, reset } = useForm({ defaultValues: { phone: '+243' } });
 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   // setOpen(true);
   reset();
  } else {

   window.console.log(data);

  };
 };

 return (
  <div className='form-input-signphone'>
   <form>
    <input type='text' name='phone' {...register('phone', { required: true })} />
   </form>
  </div>
 )
};
