import React from 'react';
import './Values.css';
import { exchAnge } from '../Fran/Input';
import Media from 'react-media';

export let newExchAnge = 0;

// Return Value Prix
export default function ReturnValues() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-values-validExh'>
  <ViewExchAnge />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-values-validExh-sm'>
  <ViewExchAnge />
 </div>
);
export const ViewExchAnge = () => {

 const [exValue, setExValue] = React.useState(exchAnge);
 newExchAnge = exValue;

 return (
  <h2>-{`${exValue}`}FC</h2>
 );
};
