import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { HiLockClosed } from 'react-icons/hi';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, updateDoc } from "firebase/firestore";
import { onSnapshot } from "firebase/firestore";
import { db } from '../../firebase';


// Return Button Component 
export default function ReturnButton() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btn-send-use'>
  <SimpleBtn />
  <CloseBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-send-use-sm'>
  <SimpleBtn />
  <CloseBtn />
 </div>
);

export const CloseBtn = () => {

 // Data first solde
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 //  order notebook
 const [tableEmCdf, setTableEmCdf] = React.useState();
 const [tableAgentCdf, setTableAgentCdf] = React.useState();

 let numPhone = window.localStorage.getItem('PHONE_SEND');
 numPhone = JSON.parse(numPhone);

 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "agent", numPhone), (doc) => {
    setFirst(doc.data().firstname);
    setLast(doc.data().lastname);
   });

   await onSnapshot(doc(db, "muungano", "emoney"), (doc) => {
    setTableEmCdf(doc.data().cdf);
   });
   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgentCdf(doc.data().cdf);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const navigation = useNavigate();
 const handlepath = (event) => {

  event.preventDefault();
  let User = first + ' ' + last;

  window.localStorage.setItem('SEND_USER', JSON.stringify(User));
  window.localStorage.setItem('SEND_EM_CDF', JSON.stringify(tableEmCdf));
  window.localStorage.setItem('SEND_EM_AGENT_CDF', JSON.stringify(tableAgentCdf));


  updateStatusAgent('close');
  navigation('/send/use-simple');
 };


 return (
  <button onClick={handlepath} className='Btn-Red'>
   Envoyer <HiLockClosed size={'1em'} />
  </button>
 );
};
export const SimpleBtn = () => {

 // Data first solde
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 let numPhone = window.localStorage.getItem('PHONE_SEND');
 numPhone = JSON.parse(numPhone);

 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "agent", numPhone), (doc) => {
    setFirst(doc.data().firstname);
    setLast(doc.data().lastname);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);


 const navigation = useNavigate();
 const handlepath = (event) => {

  event.preventDefault();

  let User = first + ' ' + last;
  window.localStorage.setItem('SEND_USER', JSON.stringify(User));

  updateStatusAgent('open');
  navigation('/send-withdraw');
 };

 return (
  <button onClick={handlepath} className='Btn'>Envoyer</button>
 );
};
export const updateStatusAgent = async (state) => {
 const agentRef = doc(db, "agent", phoneX);
 // Set the "capital" field of the city 'DC'
 await updateDoc(agentRef, {
  use: state
 });
};
