import React from 'react';
import './Input.css';
import REturnPriX from './Prix';
import Media from 'react-media';
import {
   useForm
} from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

import { doc, getDocs, collection, onSnapshot } from 'firebase/firestore';
import { db } from '../../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';


export let money = 0;
export let count = 0;
export let frais = 0;
export let solde = 0;
export let Unite = null;
export let soldePrix = 0;


// Return Phone input component
export default function REturnInputPhone() {
   return (
      <>
         <Media
            queries={{
               small: '(max-width: 599px)',
               medium: '(min-width: 600px) and (max-width:1199px)',
               large: '(min-width: 1200px)',
            }}>
            {matches => (
               <>
                  {matches.small && <ScreenSmall />}
                  {matches.medium && <ScreenLarge />}
                  {matches.large && <ScreenLarge />}
               </>
            )}
         </Media>
      </>
   );
};
export const ScreenLarge = () => {
   return (
      <FormInputValue />
   );
};
export const ScreenSmall = () => {
   return (
      <FormInputValue />
   )
};
export const FormInputValue = () => {

   const navigation = useNavigate();
   const { register, handleSubmit, watch, reset } = useForm();
   const [prix, setPrix] = React.useState(0);

   let pushUser = new Array();

   let entry = watch('count');
   let value = entry == undefined ? prix : Number(prix) - Number(entry);

   const [open, setOpen] = React.useState(false);
   const [main, setMain] = React.useState(false);
   const [high, setHigh] = React.useState(false);

   const [fullWidth, setFullWidth] = React.useState(true);
   const [maxWidth, setMaxWidth] = React.useState('sm');


   const handleClose = () => {
      setOpen(false);
   };
   const handleMain = () => {
      setMain(false);
   };
   const handleHigh = () => {
      setHigh(false);
   };


   React.useEffect(async () => {

      const querySnapshot = await getDocs(collection(db, "client"));
      querySnapshot.forEach((doc) => {
         pushUser.push(doc.id);
      });

      try {
         const unsub = onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
            setPrix(doc.data().cdf);
         });

      } catch {
         window.console.log('error');
      }


   }, []);

   const prixValue = Math.floor(value) - Math.floor(frais);

   const onSubmit = async (data) => {

      soldePrix = data.count;

      if (Number(data.count) > Number(prix)) {
         setOpen(true);
         reset();
      }
      else if (Number(data.count) > 2000000) {
         setHigh(true);
         reset();
      }
      else {

         window.console.log(data.count);
         window.localStorage.setItem('@dmsolde*#!', JSON.stringify(data.count));
         navigation('/valid-fc');
      }
   };

   return (
      <>
         <REturnPriX count={prixValue} />
         <div className='solde-input-send'>
            <h2>Votre solde en franc congolais</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
               <input type={'text'}
                  {...register('count', { required: true })}
                  name="count"
                  placeholder={'0'}
                  className='input-count'
                  autocomplete="off"
               />

               <Dialog
                  fullWidth={fullWidth}
                  maxWidth={maxWidth}
                  open={open}
                  onClose={handleClose}
               >
                  <DialogTitle>MuunganoMoney</DialogTitle>
                  <DialogContent>

                     <DialogContentText>
                        La valeur demandée n'est pas disponible, veuillez vérifier.
     </DialogContentText>

                  </DialogContent>
                  <DialogActions>
                     <Button onClick={handleClose}>Fermer</Button>
                  </DialogActions>
               </Dialog>

               <Dialog
                  fullWidth={fullWidth}
                  maxWidth={maxWidth}
                  open={main}
                  onClose={handleMain}
               >
                  <DialogTitle>MuunganoMoney</DialogTitle>
                  <DialogContent>

                     <DialogContentText>
                        Impossible de terminer cette transaction, le solde
                        principal est inférieur à 2000(CDF)
       </DialogContentText>

                  </DialogContent>
                  <DialogActions>
                     <Button onClick={handleMain}>Fermer</Button>
                  </DialogActions>
               </Dialog>

               <Dialog
                  fullWidth={fullWidth}
                  maxWidth={maxWidth}
                  open={high}
                  onClose={handleHigh}
               >
                  <DialogTitle>MuunganoMoney</DialogTitle>
                  <DialogContent>

                     <DialogContentText>
                        Désolé, nous ne pouvons pas répondre à
                        cette demande.Quantité supérieure à 2,000,000 (CDF)
       </DialogContentText>

                  </DialogContent>
                  <DialogActions>
                     <Button onClick={handleHigh}>Fermer</Button>
                  </DialogActions>
               </Dialog>

               <button className='Btn'>Suivant</button>
            </form>
         </div>
      </>
   );
};
