import React from 'react';
import './Avatar.css';
import Media from 'react-media';
import { collection, getDocs, doc, getDoc, onSnapshot } from "firebase/firestore";
import { phoneX } from '../Sign/Input';
import { db } from '../firebase';

// Avatar IMAGE VIew
export default function ReturnAvataR() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-avatar-profil'>
  <div className='avatar-circle-profil'>
   <LetteRName />
  </div>
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-avatar-profil-sm'>
  <div className='avatar-circle-profil-sm'>
   <LetteRName />
  </div>
 </div>
);
export const LetteRName = () => {

 let pushDocs = new Array();
 const [name, setName] = React.useState('M');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushDocs.push(doc.id);
  });

  const verifierInWithDocs = pushDocs.some((x) => x == phoneX);
  const unsub = onSnapshot(doc(db, verifierInWithDocs ? "client" : "agent", phoneX), (doc) => {
   setName(doc.data().firstname);
  });

 }, []);

 return (
  <div className='wrp-firstchar-avatar'>
   <h1>{name[0].toUpperCase()}</h1>
  </div>
 );
};
