 //  number = JSON.parse(window.localStorage.getItem('@dmsolde*#!'))
 //  window.console.log(number.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&, '));
 // }, []);

 let number = props.count;
 number = (number).toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&, ');


 return (
  <h2>
   <span>{number}</span>
  </h2>
 );
};
