import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { updatePassword } from "firebase/auth";
import { auth } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import ReturnQuote from './Quote';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import ReturnQuOte from './Quote';
import REturnLogo from './Logo';

import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';


// Input Field Component 
export default function ReturnInput() {
  return (
    <Media
      queries={{
        small: '(max-width: 599px)',
        medium: '(min-width: 600px) and (max-width:1199px)',
        large: '(min-width: 1200px)',
      }}>
      {matches => (
        <>
          {matches.small && <ScreenSmall />}
          {matches.medium && <SreenLarge />}
          {matches.large && <SreenLarge />}
        </>
      )}
    </Media>
  );
};
export const SreenLarge = () => (
  <div className='form-input-auth'>
    <ReturnQuote />
    <FormInput />

  </div>
);
export const ScreenSmall = () => (
  <div className='form-input-auth'>
    <ReturnQuote />
    <FormInput />
  </div>
);
export const FormInput = () => {

  const user = auth.currentUser;
  const navigation = useNavigate();
  const { register, handleSubmit, reset, control } = useForm();

  const [open, setOpen] = React.useState(false);
  const [fullWidth, setFullWidth] = React.useState(true);
  const [maxWidth, setMaxWidth] = React.useState('sm');

  const handleClose = () => {
    setOpen(false);
  };

  const onSubmit = async (data) => {
    window.console.log(user.uid);

    updatePassword(user, data.pwd).then(() => {
      // Update successful.
      window.localStorage.setItem('?bu%^%ild--#', JSON.stringify(false));
      navigation('/');

    }).catch((error) => {
      // An error ocurred
      setOpen(true);
      window.console.log(error);
    });

    reset();
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>

      <FormControl
        sx={{ width: '100%' }}

        variant="standard">
        <InputLabel htmlFor="standard-adornment-password">Mot de passe</InputLabel>

        <Controller
          name="password"
          defaultValue=''
          control={control}
          render={({ field }) =>

            <Input
              id="standard-adornment-password"
              {...field}

              style={{ textAlign: 'center' }}
              type='password'
            />}
        />

      </FormControl>

      <Dialog
        fullWidth={fullWidth}
        maxWidth={maxWidth}
        open={open}
        onClose={handleClose}
      >
        <DialogTitle>MuunganoMoney</DialogTitle>
        <DialogContent>

          <DialogContentText>
            Le mot de passe n'est pas valide
     </DialogContentText>

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Fermer</Button>
        </DialogActions>
      </Dialog>

      <button className='Btn'>Enregistre</button>
    </form>
  )
};
