import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';
import FadeLoader from 'react-spinners/FadeLoader';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';


// Firebase auth 
export var phoneX = '';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='input-phone-register'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='input-phone-register-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let pushDocs = new Array();
 let regular = /[a-z]+/;

 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm({ defaultValues: { phone: '+243' } });

 const [open, setOpen] = React.useState(false);
 const [phone, setPhone] = React.useState(false);
 const [loading, setLoading] = React.useState(false);

 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClose = () => {
  setOpen(false);
 };
 const phoneClose = () => {
  setPhone(false);
 };

 const onSubmit = async (data) => {

  setLoading(true);

  if (data.phone.length != 13 || regular.test(data.phone)) {

   window.setTimeout(() => {
    setLoading(false);
    setOpen(true);
    reset()
   }, 500);

  } else {

   const querySnapshot = await getDocs(collection(db, "agent"));
   querySnapshot.forEach((doc) => {
    pushDocs.push(doc.id);
   });

   const isPhoneInWithDocs = pushDocs.some(value => value == data.phone);
   if (isPhoneInWithDocs) {
    window.setTimeout(() => {
     setLoading(false);
     setOpen(true);
     reset()
    }, 500);

   } else {

    setLoading(false);
    window.localStorage.setItem('%%regi&*$%', JSON.stringify(true));
    let register = window.localStorage.setItem('PHONE_REGISTER', JSON.stringify(data.phone));

    phoneX = register;
    navigation('/register/signin');
   }
  };

 };
 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   {loading && <div className='App-loading-profil'></div>}
   {loading && <div className='App-Icon'>
    <FadeLoader
     size={15}
     color={'#7051B0'}
     loading={loading}
    />
   </div>}

   <FormControl
    sx={{ width: '100%' }}

    variant="standard">
    <InputLabel htmlFor="standard-adornment-password">Téléphone</InputLabel>

    <Controller
     name="phone"
     defaultValue='+243'
     control={control}
     render={({ field }) =>

      <Input
       id="standard-adornment-password"
       {...field}

       className='text-input-size'
       type='phone'

      />}
    />

   </FormControl>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le numéro de téléphone est déjà enregistré auprès de MuunganoMoney
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={phone}
    onClose={phoneClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Ce numéro de téléphone contient une erreur
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={phoneClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <button className='Btn'>Continue</button>
  </form>
 )
};
