import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, updateDoc, increment, onSnapshot, deleteDoc } from 'firebase/firestore';
import moment from 'moment';
import FadeLoader from 'react-spinners/FadeLoader';

import { db } from '../../firebase';

export let nowField = moment().date();

// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
}


export const ScreenLarge = () => (
 <div className='wrp-btn-delete'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-delete-sm'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);

export const CancelBtn = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('rm #@!!/[] -', JSON.stringify(null));
  window.localStorage.setItem('@st#!--%#', JSON.stringify(null));
  navigation('/dash');
 }

 return (
  <button onClick={handlepath} className='Btn'>Anuller</button>
 );
};
export const DeleteBtn = () => {

 const [listAgent, setListAgent] = React.useState();
 const [listClient, setListClient] = React.useState();
 const [loading, setLoading] = React.useState(true);

 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setListAgent(doc.data().agent);
    setListClient(doc.data().client);
   });

  } catch {
   window.console.log('error');
  }

 }, []);

 let phoneNum = JSON.parse(window.localStorage.getItem('rm #@!!/[] -'));
 let status = JSON.parse(window.localStorage.getItem('@st#!--%#'));

 const handleDelete = async (event) => {

  event.preventDefault();

  let indexAgent = Number(listAgent[nowField]) - 1;
  let indexClient = Number(listClient[nowField]) - 1;

  let arrayAgent = listAgent;
  let arrayClient = listClient;

  for (let i = nowField; i <= 31; i++) {
   arrayAgent[i] = indexAgent;
   arrayClient[i] = indexClient;
  }

  if (status == "agent") {
   incrementPartnerAgent(arrayAgent);
  } else {
   incrementPartnerClient(arrayClient);
  }

  await deleteDoc(doc(db, `${status}`, phoneNum));

  // window.setTimeout(() => {
  // }, 650);

  window.setTimeout(() => {
   setLoading(false);
   window.location.href = "/dash";
  }, 675);

  window.localStorage.setItem('rm #@!!/[] -', JSON.stringify(null));
  window.localStorage.setItem('@st#!--%#', JSON.stringify(null));
 }

 return (
  <>
   {loading && <div className='App-loading-Actif'></div>}
   {loading && <div className='App-Icon'>
    <FadeLoader
     size={15}
     color={'#00b8d4'}
     loading={loading}
    />
   </div>}

   <button onClick={handleDelete} className='Btn-Red'>Supprimer</button>
  </>
 );
};

async function incrementPartnerAgent(arrayAgent) {

 const washingtonRef = doc(db, "muungano", "partner");
 await updateDoc(washingtonRef, {
  agent: arrayAgent,
 });
 const partnerRef = doc(db, "muunganomoney", "partner");
 await updateDoc(partnerRef, {
  agent: increment(-1),
 });

};
async function incrementPartnerClient(arrayClient) {

 const washingtonRef = doc(db, "muungano", "partner");
 await updateDoc(washingtonRef, {
  client: arrayClient,
 });
 const partnerRef = doc(db, "muunganomoney", "partner");
 await updateDoc(partnerRef, {
  client: increment(-1),
 });

};
