import React from 'react';
import './InputPhone.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

// Firebase Auth for phone
import { getAuth, signInWithPhoneNumber, RecaptchaVerifier } from "firebase/auth";
import { auth } from '../firebase';


// Input Field form
export default function REturnInPutCode() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='form-input-sign'>
  <FormDataInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-sign-sm'>
  <FormDataInput />
 </div>
);

export const FormDataInput = () => {

 const [checked, setChecked] = React.useState(true);
 const { register, handleSubmit, formState, reset } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });


 // window.recaptchaVerifier = new RecaptchaVerifier('recaptcha-container', {}, auth);
 // const appVerifier = window.recaptchaVerifier;


 const { isSubmitSuccessful } = formState;
 const navigation = useNavigate();



 const onsubmit = async data => {

  signInWithPhoneNumber(auth, data.phone)
   .then((confirmationResult) => {

    window.confirmationResult = confirmationResult;


   }).catch((error) => {
    // Error; SMS not sent
    window.console.log(error.message);
   });


  setChecked(false);
  window.console.log(data);
  reset();



 };
 const onRegister = async data => {
  console.log(data);
  navigation('/register');
 };

 return (
  <>
   {checked ?
    <form onSubmit={handleSubmit(onsubmit)}>
     <input type='phone' id='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
     <div className='recaptcha-container'></div>
     <button disabled={isSubmitSuccessful} className='Btn' >Suivant</button>
    </form>
    :
    <form onSubmit={handleSubmit(onRegister)}>
     <input type='text' placeholder='Valider Code' name='code' {...register('code', { required: true })} />
     <button className='Btn' >Connexion</button>
    </form>
   }
  </>
 );
};
