import React from 'react';
import './Logo.css';
import Media from 'react-media';
import { TweenMax, Power0 } from 'gsap';

// Return logo view 
export default function REturnlogo() {

 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => {
 return (
  <div className='wrp-logo-sign'>
   <ViewLogo />
  </div>
 );
};
export const ScreenSmall = () => {
 return (
  <div className='wrp-logo-sign-sm'>
   <ViewLogo />
  </div>
 );
}
export const ViewLogo = () => {

 React.useEffect(() => {
  TweenMax.to('.Logo', 1, { delay: .5, opacity: 1, y: -45, ease: Power0.easeIn });
 }, []);

 return (
  <img src={'/img/logo.png'} alt={'logo view '} className='Logo' />
 )
}
