import React from 'react';
import './Btn.css';
import { IoIosPaperPlane } from 'react-icons/io';
import { IoCheckmark } from 'react-icons/io5';
import { TweenMax, Expo } from 'gsap';


// Global checked success
export const checkedSucess = true;


// Button Connexion Valid 
export default function ReturnButton() {

 const [valid, setValid] = React.useState(true);
 const handleValid = (event) => {
  event.preventDefault();
  setValid(false);
  checkedSucess = valid;
 };

 React.useEffect(() => {
  TweenMax.from('.wrp-btn-valid-succes', 1.2, { delay: 1, opacity: 0, y: 0, ease: Expo.easeIn });

 }, []);

 return (
  <div className='wrp-btn-exchange'>
   {valid ?
    <button onClick={handleValid} className='Btn'>
     <span>Valider Transaction</span>
     <IoIosPaperPlane size={'1em'} />
    </button>
    :
    <div className='wrp-btn-valid-succes'>
     <div className='CheckMark'>
      <IoCheckmark size={'3em'} />
     </div>
    </div>
   }
  </div>
 );
};
