import React from 'react';
import './Basic.css';
import ReturnHeAd from './Head';
import ReturnText from './Text';
import { collection, getDocs, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';


let pushDocs = new Array();

// Return Box 
export default function BasicInFo() {

 const [phone, setPhone] = React.useEffect(null);
 const [mail, setMail] = React.useEffect(null);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  const collections = arrayClient.some(value => value == JSON.parse(window.localStorage.getItem('%^info&*ºº––')));
  const unsub = onSnapshot(doc(db, collections ? "client" : "agent", JSON.parse(window.localStorage.getItem('%^info&*ºº––'))), (doc) => {
   setPhone(doc.data().phone);
   setMail(doc.data().email);
  });

 }, []);

 return (
  <div className='basic-info'>
   <ReturnHeAd Text='basic information' />

   <div className='info-flex-gap'>
    <ReturnText subject='Phone' Info={phone} />
    <ReturnText subject='Email' Info={mail} />
   </div>

  </div>
 );
};
