import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';
import { db } from '../../firebase';

import ReturnQuOte from './Quote';
import moment from 'moment';
import 'moment/locale/fr';

export let nowField = moment().date();
export let now = moment().date();
export let prixSuccess = 0;

var regular = /[a-z]+/;

// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <ReturnQuOte />
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <ReturnQuOte />
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();

 const { handleSubmit, register, reset } = useForm();
 const [getPin, setGetPin] = React.useState('');
 const [solde, setSolde] = React.useState(null);

 const [open, setOpen] = React.useState(false);
 const [passe, setPasse] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');


 const handleClose = () => {
  setOpen(false);
 };

 const handlePasseClose = () => {
  setPasse(false);
 };

 React.useEffect(async () => {

  try {

   await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
    setGetPin(doc.data().code);
    setSolde(doc.data().cdf);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  const phoneItem = window.localStorage.getItem('PHONE_SEND');
  const sendEMCdf = window.localStorage.getItem('SEND_EM_CDF');
  const sendEMAgentCdf = window.localStorage.getItem('SEND_EM_AGENT_CDF');
  const sendUser = window.localStorage.getItem('SEND_USER');

  if (getPin == data.code) {

   if (data.count > 1000 || data.count < 1 || solde < 10 || data.count > solde || regular.test(data.count)) {
    setOpen(true);
    reset();
   } else {

    // let User = first + ' ' + last;
    sendAgentMoney(Math.floor(Number(data.count)), JSON.parse(phoneItem));
    updateAdmin(Number(data.count), Math.floor(Number(data.count)), JSON.parse(sendUser));

    let arraySendEMCdf = JSON.parse(sendEMCdf);
    let arraySendEMAgentCdf = JSON.parse(sendEMAgentCdf);

    let soldeEmCDF = Number(Math.max(...arraySendEMCdf)) + Number(data.count);
    let soldeAgentCDF = Number(Math.max(...arraySendEMAgentCdf)) + Number(data.count);

    let arrayEmCdf = arraySendEMCdf;
    let arrayAgentCdf = arraySendEMAgentCdf;

    for (let i = now; i <= 31; i++) {
     arrayEmCdf[i] = soldeEmCDF;
     arrayAgentCdf[i] = soldeAgentCDF;
    }

    prixSuccess = data.count;

    updateMuungano(arrayEmCdf, arrayAgentCdf);
    window.localStorage.setItem('PHONE_SEND', JSON.stringify(null));
    window.localStorage.setItem('SEND_EM_CDF', JSON.stringify(null));
    window.localStorage.setItem('SEND_EM_AGENT_CDF', JSON.stringify(null));
    window.localStorage.setItem('#62^^ts@&', JSON.stringify(false));

    navigation('/send/success-usd');
   }
  } else {
   prixSuccess = data.count;
   setPasse(true);
   reset();
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'USD'}
   />
   <input type={'password'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
    className='letter-space-pwd'
   />
   <button className='Btn'>Envoyer</button>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={passe}
    onClose={handlePasseClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Veuillez vérifier le mot de passe administrateur
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handlePasseClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le solde entre n'est pas valide, veuillez vérifier
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 );
};

async function sendAgentMoney(solde, numPhone) {

 let time = moment().format('LLL');
 let send = { date: time, solde: `${solde} USD`, phone: numPhone, user: 'muunganomoney', type: 'réussi' }

 const washingtonRef = doc(db, "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(solde),
  money: solde,
  unite: 'USD',
  user: 'muunganomoney',
  delay: moment().format(),
  swap: arrayUnion(send)
 });

};
async function updateAdmin(solde, money, User) {

 const washingtonRef = doc(db, "muunganomoney", "money");
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(-solde),
  money: money,
  buyusd: solde,
  unite: 'USD',
  user: User,
  delay: moment().format()
 });

 const emoneyRef = doc(db, "muunganomoney", "e-money");
 await updateDoc(emoneyRef, {
  usd: solde
 });

 const moneyRef = doc(db, "muunganomoney", "money");
 await updateDoc(moneyRef, {
  usd: increment(-solde)
 });

};
async function updateMuungano(emUsd, agentUsd) {

 const emoneyRef = doc(db, "muungano", "emoney");
 await updateDoc(emoneyRef, {
  usd: emUsd
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: agentUsd
 });

};
