import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, deleteDoc } from "firebase/firestore";
import { db } from '../../firebase';


// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
}


export const ScreenLarge = () => (
 <div className='wrp-btn-delete'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-delete-sm'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);

export const CancelBtn = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('rm #@!!/[] -', JSON.stringify(null));
  window.localStorage.setItem('@st#!--%#', JSON.stringify(null));
  navigation('/dash');
 }

 return (
  <button onClick={handlepath} className='Btn'>Anuller</button>
 );
};
export const DeleteBtn = () => {


 const [listAgent, setListAgent] = React.useState();
 const [listClient, setListClient] = React.useState();

 React.useEffect(() => { }, []);


 let phoneNum = JSON.parse(window.localStorage.getItem('rm #@!!/[] -'));
 let status = JSON.parse(window.localStorage.getItem('@st#!--%#'));

 const handleDelete = async (event) => {

  event.preventDefault();
  await deleteDoc(doc(db, `${status}`, phoneNum));
  window.setTimeout(() => {
   window.location.href = "/dash";
  }, 675);

  window.localStorage.setItem('rm #@!!/[] -', JSON.stringify(null));
  window.localStorage.setItem('@st#!--%#', JSON.stringify(null));
 }

 return (
  <button onClick={handleDelete} className='Btn-Red'>Supprimer</button>
 );
};
