import React from 'react';
import ReturnCurrent from './Current';
import GridLoader from 'react-spinners/GridLoader';
import { auth } from '../firebase';
import SignUp from '../Sign/Main';

// Current Main 
export default function CuRRent() {

 const [loading, setLoading] = React.useState(false);
 const user = auth.currentUser;

 React.useEffect(() => {

  setLoading(!!user.uid);
  setTimeout(() => {
   setLoading(false);
  }, 3600);

 }, []);

 return (
  <div>
   {
    loading ?
     <div className='App-loading'>
      <GridLoader
       size={15}
       color={'#00b8d4'}
       loading={loading}
      />
     </div>
     :
     <>
      {!!user.uid ? <ReturnCurrent /> : <SignUp />}
     </>
   }
  </div>
 );
};
