import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, setDoc, updateDoc, increment, onSnapshot } from 'firebase/firestore';
import { db } from '../../firebase';
// import dayjs from 'dayjs';
import MomentUtils from "@date-io/moment";
// import { phoneX } from '../Phone/Input';
import moment from 'moment';
import "moment/locale/fr";

// import DateFnsUtils from '@date-io/date-fns'; // choose your lib
import {
 DatePicker,
 MuiPickersUtilsProvider,
} from '@material-ui/pickers';

export let nowField = moment().date();

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => {
 return (
  <div className='view-input-signin-register'>
   <FormInput />

  </div>
 )
}
export const ScreenSmall = () => {
 return (
  <div className='view-input-signin-register-sm'>
   <FormInput />

  </div>
 )
};
export const FormInput = () => {

 let regular = /[a-z]+/;
 const [locale, setLocale] = React.useState("fr");
 const [tablepartner, setTablepartner] = React.useState();
 const [selectedDate, handleDateChange] = React.useState(new Date());

 const navigation = useNavigate();
 const existing = { state: "agent", cdf: 0, usd: 0, thriftcdf: 0, thriftusd: 0, accountdate: moment().format(), delay: '', code: 'muuganomoney', phoneclient: '', unite: '', solde: 0, money: 0, frais: 0, user: '', swap: [] }
 const { register, handleSubmit, control } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });


 // Ref collection database!
 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setTablepartner(doc.data().agent);
   });
  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   window.alert('Numéro de téléphone est Incorrect!');

  } else {

   let valuePartner = Number(tablepartner[nowField]) + 1;
   let arrayPartner = tablepartner;

   for (let i = nowField; i <= 31; i++) {
    arrayPartner[i] = valuePartner;
   }

   let dateBirth = data.birth.format('LL');
   Object.defineProperty(data, 'birth', {

    value: dateBirth,
    writable: true,
    configuration: true,
    enumerable: true

   })

   let phoneX = window.localStorage.getItem('PHONE_REGISTER');
   window.localStorage.setItem('PHONE_REGISTER', null);

   const clone = Object.assign(data, existing);

   saveInfoInWithDocs(JSON.parse(phoneX), clone);
   incrementPartner(arrayPartner);
   navigation('/register/success');


  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='Nom' name='firstname' {...register('firstname', { required: true })} />
   <input type='text' placeholder='Prènom' name='lastname' {...register('lastname', { required: true })} />
   <input type='email' placeholder='Email' name='email' {...register('email', { required: false })} />
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   <input type='text' placeholder='Nom du Business' name='job' {...register('job', { required: true })} />
   <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
   <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />

   <Controller
    name="birth"
    control={control}
    render={({ field }) =>
     <MuiPickersUtilsProvider
      libInstance={moment} utils={MomentUtils} locale={locale}>

      <DatePicker
       disableFuture
       openTo="year"
       format="dd/MM/yyyy"
       label="Date de naissance"
       views={["year", "month", "date"]}
       value={selectedDate}
       onChange={handleDateChange}
       {...field}
      />
     </MuiPickersUtilsProvider>}
   />
   <button className='Btn'>Enregistre</button>
  </form>
 )
};

async function saveInfoInWithDocs(phoneInDocs, user) {
 await setDoc(doc(db, "agent", phoneInDocs), user);
};
async function incrementPartner(arrayPartner) {

 const washingtonRef = doc(db, "muungano", "partner");
 await updateDoc(washingtonRef, {
  agent: arrayPartner,
 });

 const partnerRef = doc(db, "muunganomoney", "partner");
 await updateDoc(partnerRef, {
  agent: increment(1),
 });

};
