import React from 'react';
import './Prix.css';
import { TweenLite } from 'gsap';

// Prix View Component 
export default function ReturnPriX() {

 const [value, setValue] = React.useState(22345);
 const obj = { value: 0 };
 React.useEffect(() => {
  TweenLite.to(obj, 5, {
   onUpdate: () => {
    setValue(obj.value)
   }
  })

 }, []);

 return (
  <div className='wrp-prix-exchange'>
   <p>{`${value}FC`}</p>
  </div>
 );
};
