import React from 'react';
import './BtnAdd.css';
import { doc, updateDoc } from "firebase/firestore";
import { db } from '../firebase';
import Media from 'react-media';

// Return Add Button
export default function ReturnAddBtnMoney() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btnadd-dash'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btnadd-dash-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 // Add money Ungama
 const addMoneyInWithUngamaMoney = async () => {

  let cdf = 50000;
  let usd = 500;

  const docRef = doc(db, "ungama", "money");
  await updateDoc(docRef, {
   fc: cdf,
   usd: usd
  });

  window.console.log(cdf + ' ' + usd);
 }

 return (
  <button className='Btn'
  // onClick={addMoneyInWithUngamaMoney()}
  >Ajouter Money</button>
 )
};
