import './Unite.css';
import Media from 'react-media';

// Return Unite
export default function ReturnUnite(props) {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall Text={props.Text} />}
     {matches.medium && <ScreenLarge Text={props.Text} />}
     {matches.large && <ScreenLarge Text={props.Text} />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = (props) => (
 <div className='wrp-unite-dash'>
  <p>{props.Text}</p>
 </div>
);
export const ScreenSmall = (props) => (
 <div className='wrp-unite-dash-sm'>
  <p>{props.Text}</p>
 </div>
);
