import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, updateDoc, getDoc } from "firebase/firestore";
import { db } from '../firebase';

// Firebase auth 
export let phoneX = '';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-add-dash'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-add-dash-sm'>
  <FormInput />
 </div>
);

export const FormInput = () => {

 let newCdf = 0;
 let newUsd = 0;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 const [soldeCdf, setSoldeCdf] = React.useState(0);
 const [soldeUsd, setSoldeUsd] = React.useState(0);
 const [getCode, setGetCode] = React.useState('');

 React.useEffect(async () => {

  const docRef = doc(db, "ungama", "money");
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setSoldeCdf(docSnap.data().fc)
   setSoldeUsd(docSnap.data().usd);
   setGetCode(docSnap.data().pin);
  }

 }, []);

 const onSubmit = async (data) => {

  if (data.code == getCode) {

   if (Number(data.fran) > 300000 || Number(data.dollar) > 3000) {

    window.alert(`Le montant n'est pas autorisé`);
    reset();
   } else {
    newCdf = Number(soldeCdf) + Number(data.fran);
    newUsd = Number(soldeUsd) + Number(data.dollar);

    updateAddMoney(newCdf, newUsd);
    navigation('/dash');
   }
  } else {
   window.alert(`Code Invalide`);
   reset();
  }

  reset();
 };
 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='USD' name='dollar' {...register('dollar', { required: true })} />
   <input type='text' placeholder='CDF' name='fran' {...register('fran', { required: true })} />
   <input type='text' placeholder='••••••' name='code' {...register('code', { required: true })} />
   <button className='Btn'>Ajouter</button>
  </form>
 )
};

// Update input arrow
export const updateAddMoney = async (prixCdf, prixUsd) => {
 const washingtonRef = doc(db, "ungama", "money");
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  fc: prixCdf,
  usd: prixUsd,
 });
};
