import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";

import { db } from '../../firebase';
import REturnQuOte from './Quote';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

import moment from 'moment';

export let nowField = moment().date();
export let now = moment().date();

export let prixHash = 0;
export let hashTrue = true;

// Input Field Component 
export default function ReturnInput() {
  return (
    <Media
      queries={{
        small: '(max-width: 599px)',
        medium: '(min-width: 600px) and (max-width:1199px)',
        large: '(min-width: 1200px)',
      }}>
      {matches => (
        <>
          {matches.small && <ScreenSmall />}
          {matches.medium && <SreenLarge />}
          {matches.large && <SreenLarge />}
        </>
      )}
    </Media>
  );
};
export const SreenLarge = () => (
  <div className='wrp-form-input-pin'>
    <REturnQuOte />
    <FormInput />

  </div>
);
export const ScreenSmall = () => (
  <div className='wrp-form-input-pin'>
    <REturnQuOte />
    <FormInput />
  </div>
);

export const FormInput = () => {

  const navigation = useNavigate();

  const { handleSubmit, register, reset, control } = useForm({});
  const [getPin, setGetPin] = React.useState('');
  const [emoney, setEmoney] = React.useState([]);
  const [agentsemoney, setAgentsEmoney] = React.useState([]);

  const [open, setOpen] = React.useState(false);

  const [fullWidth, setFullWidth] = React.useState(true);
  const [maxWidth, setMaxWidth] = React.useState('sm');

  const [showPassword, setShowPassword] = React.useState(false);

  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };


  const handleClose = () => {
    setOpen(false);
  };

  React.useEffect(async () => {

    try {

      await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
        setGetPin(doc.data().code);
      });

    } catch {
      window.console.log('erreur disponible');
    }
    try {

      await onSnapshot(doc(db, "muungano", "emoney"), (doc) => {
        setEmoney(doc.data().cdf);
      });

    } catch {
      window.console.log('erreur disponible');
    }
    try {

      await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
        setAgentsEmoney(doc.data().cdf);
      });

    } catch {
      window.console.log('erreur disponible');
    }

  }, []);

  const onSubmit = async (data) => {

    const numPhone = JSON.parse(window.localStorage.getItem('**adm@@ph$$&-@#'));
    const prixSolde = JSON.parse(window.localStorage.getItem('@dmsolde*#!'));
    const nameUser = JSON.parse(window.localStorage.getItem('#adX**nam**'));
    const pass = JSON.parse(window.localStorage.getItem('#adX**˙´ð..¸'));

    prixHash = numPhone;

    if (data.code != getPin) {
      setOpen(true);
      reset();
    } else {

      sendAgentMoney(prixSolde, numPhone);
      updateAdmin(prixSolde, nameUser);


      let arraySendEMCdf = emoney;
      let arraySendEMAgentCdf = agentsemoney;

      let soldeEmCDF = Number(Math.max(...arraySendEMCdf)) + Number(prixSolde);
      let soldeAgentCDF = Number(Math.max(...arraySendEMAgentCdf)) + Number(prixSolde);

      let arrayEmCdf = arraySendEMCdf;
      let arrayAgentCdf = arraySendEMAgentCdf;

      for (let i = now; i <= 31; i++) {
        arrayEmCdf[i] = soldeEmCDF;
        arrayAgentCdf[i] = soldeAgentCDF;
      }

      updateMuungano(arrayEmCdf, arrayAgentCdf);
      window.localStorage.setItem('#adX**˙´ð..¸', JSON.stringify(false));
      navigation('/send/success');
    }

  };


  return (
    <form onSubmit={handleSubmit(onSubmit)}>

      <FormControl
        sx={{ width: '100%' }}

        variant="standard">
        <InputLabel htmlFor="standard-adornment-password">Pin actuel</InputLabel>

        <Controller
          name="code"
          control={control}
          render={({ field }) =>

            <Input
              id="standard-adornment-password"
              autocomplete="new-password"
              {...field}
              type={showPassword ? 'text' : 'password'}

              endAdornment={
                <InputAdornment position="end">

                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                  >
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>

                </InputAdornment>
              }

            />}
        />
      </FormControl>


      <Dialog
        fullWidth={fullWidth}
        maxWidth={maxWidth}
        open={open}
        onClose={handleClose}
      >
        <DialogTitle>MuunganoMoney</DialogTitle>
        <DialogContent>

          <DialogContentText>
            Le mot de passe administrateur est invalide!
     </DialogContentText>

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Fermer</Button>
        </DialogActions>
      </Dialog>


      <button className='Btn'>Envoi</button>
    </form>
  )
};



async function sendAgentMoney(solde, numPhone) {

  let time = moment().format('LLL');
  let send = { date: time, solde: `${solde} CDF`, phone: numPhone, user: 'muunganomoney', type: 'réussi' }

  const washingtonRef = doc(db, "agent", numPhone);
  // Set the "capital" field of the city 'DC'
  await updateDoc(washingtonRef, {
    cdf: increment(Number(solde)),
    money: Number(solde),
    unite: 'CDF',
    user: 'muunganomoney',
    delay: moment().format(),
    swap: arrayUnion(send)
  });
};
async function updateAdmin(solde, User) {

  const washingtonRef = doc(db, "muunganomoney", "money");
  // Set the "capital" field of the city 'DC'
  await updateDoc(washingtonRef, {
    cdf: increment(-(Number(solde))),
    money: Number(solde),
    // buycdf: solde,
    unite: 'CDF',
    user: User,
    delay: moment().format()
  });

};
async function updateMuungano(emCdf, agentCdf) {

  const emoneyRef = doc(db, "muungano", "emoney");
  await updateDoc(emoneyRef, {
    cdf: emCdf,
  });

  const agentsemoneyRef = doc(db, "agents", "emoney");
  await updateDoc(agentsemoneyRef, {
    cdf: agentCdf,
  });

};
