import React from 'react';
import './Title.css';
import { TweenMax, Expo } from 'gsap';
import Media from 'react-media';

// View Test comppoent 
export default function ReturnTitle() {
 return (
  <Media queries={{
   small: "(max-width: 599px)",
   medium: "(min-width: 600px) and (max-width: 1199px)",
   large: "(min-width: 1200px)"
  }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 )
};
export const ScreenLarge = () => {

 React.useEffect = (() => {
  TweenMax.from('.wrp-title-home h1', 2.2, { delay: 0.4, y: 80, opacity: 0, ease: Expo.easeInOut })
 }, []);

 return (
  <div className='wrp-title-home'>
   <h1>Bienvenue espace Admin</h1>
   <p>Heureux de vous revoir ! Connectez-vous pour utiliser l'application.</p>
  </div>
 );
}

export const ScreenSmall = () => {
 return (
  <div className='wrp-title-home-sm'>
   <h1>Bienvenue espace Admin</h1>
   <p>Heureux de vous revoir ! Connectez-vous pour utiliser l'application.</p>
  </div>
 )
}
