import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, getDoc, updateDoc, increment, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';

import { phoneX } from '../../../Sign/Input';
import { sendPhone } from '../../../ValidExFran/Input';
import { frais, Unite, solde, money } from '../../../FC/Input';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import moment from 'moment';
export let nowField = (moment().date()) - 1;

export let prixHash = 0;
export let hashTrue = true;

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-send-money'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-send-money-sm'>
  <FormInput />
 </div>
);

export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 //  order notebook
 const [tableClient, setTableClient] = React.useState();
 const [tableAgent, setTableAgent] = React.useState();
 const [tableUpgrade, setTableUpgrade] = React.useState();
 const [tableAdmin, setTableAdmin] = React.useState();

 // send React useState  
 const [pin, setPin] = React.useState(null);
 const [state, setState] = React.useState(null);
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 // getting React useState
 const [getstate, setGetstate] = React.useState(null);
 const [getfirst, setGetFirst] = React.useState('');
 const [getlast, setGetLast] = React.useState('');


 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };


 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  verifierSend = pushDocs.some(value => value == phoneX);
  verifierGet = pushDocs.some(value => value == sendPhone);

  const sendRef = doc(db, verifierSend ? "client" : "agent", phoneX);
  const sendSnap = await getDoc(sendRef);

  if (sendSnap.exists()) {
   setPin(sendSnap.data().code);
   setState(sendSnap.data().state);
   setFirst(sendSnap.data().firstname);
   setLast(sendSnap.data().lastname);
  };

  const getRef = doc(db, verifierGet ? "client" : "agent", sendPhone);
  const getSnap = await getDoc(getRef);

  if (getSnap.exists()) {
   setGetstate(getSnap.data().state);
   setGetFirst(getSnap.data().firstname);
   setGetLast(getSnap.data().lastname);
  };

  try {
   await onSnapshot(doc(db, "clients", "emoney"), (doc) => {
    setTableClient(doc.data().cdf);
   });

   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgent(doc.data().cdf);
   });

   await onSnapshot(doc(db, "agents", "commission"), (doc) => {
    setTableAdmin(doc.data().cdf);
   });

   await onSnapshot(doc(db, "muungano", "upgrade"), (doc) => {
    setTableUpgrade(doc.data().cdf);
   });

  } catch {
   window.console.log('Erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  // Checked if value code is length valid
  if (data.code.length != 6 || pin != data.code) {

   setOpen(true);
   reset();

  } else {

   const sendUser = first.toLowerCase() + ' ' + last.toLowerCase();
   const getUser = getfirst.toLowerCase() + ' ' + getlast.toLowerCase();

   if (state == "agent" && getstate == "agent") {

    hashTrue = false;
    prixHash = solde;

    let arrayAgent = tableAgent;
    let arrayUpgrade = tableUpgrade;

    arrayAgent[nowField] = Number(tableAgent[nowField]) + solde;
    arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + solde;

    swapInWithDocsAgent(phoneX, sendPhone, sendUser, getUser, solde, Unite, arrayAgent, arrayUpgrade);
    navigation('/send-success');


   } else if (state == "client" && getstate == "client") {

    hashTrue = true;
    prixHash = money;

    let arrayClient = tableClient;
    let arrayUpgrade = tableUpgrade;
    let arrayAdmin = tableAdmin;

    window.console.log(arrayAdmin);

    // arrayClient[nowField] = Number(tableClient[nowField]) + Number(money);
    // arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + Number(money);
    // arrayAdmin[nowField] = Number(tableAdmin[nowField]) + Number(frais);

    // isSwapInWithClientToClient(phoneX, sendPhone, sendUser, getUser, solde, money, Unite, arrayClient, arrayUpgrade, arrayAdmin);
    // navigation('/send-success');

   } else {

    if (state == "agent") {

     hashTrue = false;
     prixHash = solde;

     let arrayAgent = tableAgent;
     let arrayUpgrade = tableUpgrade;

     arrayAgent[nowField] = Number(tableAgent[nowField]) + solde;
     arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + solde;

     swapInWithDocsAgentToClient(phoneX, sendPhone, sendUser, getUser, solde, Unite, arrayAgent, arrayUpgrade);

     navigation('/send-success');

    } else {

     hashTrue = true;
     prixHash = money;

     let arrayClient = tableClient;
     let arrayUpgrade = tableUpgrade;

     arrayClient[nowField] = Number(tableClient[nowField]) + money;
     arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + money;

     isSwapInWithClientToAgent(phoneX, sendPhone, sendUser, getUser, solde, money, Unite, arrayClient, arrayUpgrade);
     navigation('/send-success');
    }

   }

  }
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='password'
    placeholder='••••••'
    name='code' {...register('code', { required: true })}
   />

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Code d'accès refusé. Veuillez vérifier
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>


   <button className='Btn'>Valider</button>
  </form>
 )
};


