import React from 'react';
import './Prix.css';
import { Fc, Usd } from '../../../Money';
import { phoneX } from '../../../Sign/Input';
import { collection, getDocs, getDoc, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';
import {TweenMax} from 'gsap';


// Prix HeAd 
export default function ReturnPrix() {

 let pushDocs = new Array();
 const [fc, setFc] = React.useState(Fc);
 const [usd, setUsd] = React.useState(Usd);

 React.useEffect(async () => {

  const querySnapshotClient = await getDocs(collection(db, "client"));
  querySnapshotClient.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  var verifierCollection = pushDocs.some((value) => value == phoneX);
  const unsub = onSnapshot(doc(db, verifierCollection ? "client" : "agent", phoneX), (doc) => {
   setFc(doc.data().cdf);
   setUsd(doc.data().usd);
  });


 }, []);

 return (
  <div className='wrp-main-prix-dash'>
   <ReturnPrixUseR prix={usd} unite={'USD'} coloor={true} coolorUnite={true} />
   <ReturnPrixUseR prix={fc} coloor={true} coolorUnite={true} unite={'CDF'} />
  </div>
 );
};
export function ReturnPrixUseR(props) {
 return (
  <div className='wrp-prix-dash'>
   <span style={{ color: `${props.coloor == true ? '#ffeb3b' : '#9e9e9e'}` }}>{props.prix}</span>
   <span style={{ color: `${props.coolorUnite == true ? '#ffeb3b' : '#9e9e9e'}` }}>{props.unite}</span>
  </div>
 );
};
