import React from 'react';
import './Prix.css';
import { TweenLite } from 'gsap';
import { newExchAnge } from '../ValidExchange/Values';

import { checkedSucess } from './Btn';

// Prix View Component 
export default function ReturnPriX() {

 const [withdraw, setWithdraw] = React.useState(newExchAnge);
 const obj = { value: 0 };

 React.useEffect(() => {
  let target = document.querySelector('.valueTarget');
  TweenLite.to(obj, 1, {
   value: Math.floor(newExchAnge),

   roundProps: { value: 1 },
   onUpdate: () => target.innerHTML = obj.value
  })
  console.log(withdraw, + ' ' + checkedSucess);
 }, []);

 return (
  <div className='wrp-prix-exchange'>
   <p className='valueTarget'
    style={{ color: `${checkedSucess ? 'blue' : 'crimson'}` }}>
   </p>
  </div>
 );
};
