import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';
import FadeLoader from 'react-spinners/FadeLoader';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

// Firebase auth 
export var phoneX = '';
export var state = '';

// Input Field Component 
export default function ReturnInput() {
  return (
    <Media
      queries={{
        small: '(max-width: 599px)',
        medium: '(min-width: 600px) and (max-width:1199px)',
        large: '(min-width: 1200px)',
      }}>
      {matches => (
        <>
          {matches.small && <ScreenSmall />}
          {matches.medium && <SreenLarge />}
          {matches.large && <SreenLarge />}
        </>
      )}
    </Media>
  );
};
export const SreenLarge = () => (
  <div className='wrp-input-phone-register'>
    <FormInput />

  </div>
);
export const ScreenSmall = () => (
  <div className='wrp-input-phone-register-sm'>
    <FormInput />
  </div>
);
export const FormInput = () => {

  let clientDocs = new Array();
  let agentDocs = new Array();

  let regular = /[a-z]+/;
  const [loading, setLoading] = React.useState(false);

  const navigation = useNavigate();
  const { register, handleSubmit, reset } = useForm({ defaultValues: { phone: '+243' } });

  const [open, setOpen] = React.useState(false);
  const [fullWidth, setFullWidth] = React.useState(true);
  const [maxWidth, setMaxWidth] = React.useState('sm');

  const handleClose = () => {
    setOpen(false);
  };

  const onSubmit = async (data) => {
    setLoading(true);

    if (data.phone.length != 13 || regular.test(data.phone)) {

      window.setTimeout(() => {
        setLoading(false);
        setOpen(true);
        reset()
      }, 500);

    } else {

      const clientSnapshot = await getDocs(collection(db, "client"));
      clientSnapshot.forEach((doc) => {
        clientDocs.push(doc.id);
      });

      const agentSnapshot = await getDocs(collection(db, "agent"));
      agentSnapshot.forEach((doc) => {
        agentDocs.push(doc.id);
      });

      const isPhoneInWithDocsClient = clientDocs.some(value => value == data.phone);
      const isPhoneInWithDocsAgent = agentDocs.some(value => value == data.phone);

      if (isPhoneInWithDocsClient || isPhoneInWithDocsAgent) {
        setLoading(false);

        phoneX = data.phone;
        window.localStorage.setItem('%^info&*ºº––', JSON.stringify(data.phone));
        navigation('/info/people');

      } else {

        setLoading(false);
        setOpen(true);
        reset()
      }
    };

    setLoading(false);
    reset();
  };
  return (
    <>
      {loading && <div className='App-loading-profil'></div>}
      {loading && <div className='App-Icon'>
        <FadeLoader
          size={15}
          color={'#7051B0'}
          loading={loading}
        />
      </div>}

      <form onSubmit={handleSubmit(onSubmit)}>
        <input type='text' placeholder='Phone' name='phone' {...register('phone', { required: true })} />

        <FormControl
          sx={{ width: '100%' }}

          variant="standard">
          <InputLabel htmlFor="standard-adornment-password">Téléphone</InputLabel>

          <Controller
            name="phone"
            defaultValue='+243'
            control={control}
            render={({ field }) =>

              <Input
                id="standard-adornment-password"
                {...field}

                className='text-input-size'
                type='phone'

              />}
          />

        </FormControl>



        <Dialog
          fullWidth={fullWidth}
          maxWidth={maxWidth}
          open={open}
          onClose={handleClose}
        >
          <DialogTitle>MuunganoMoney</DialogTitle>
          <DialogContent>

            <DialogContentText>
              Numéro non disponible chez muunganomoney
     </DialogContentText>

          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Fermer</Button>
          </DialogActions>
        </Dialog>

        <button className='Btn'>Continue</button>
      </form>
    </>
  )
};
