import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, deleteDoc, getDocs, collection } from "firebase/firestore";
import { db } from '../../firebase';
import moment from 'moment';

let arrayPush = new Array();
export let nowField = moment().date();


// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
}


export const ScreenLarge = () => (
 <div className='wrp-btn-delete'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-delete-sm'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);

export const CancelBtn = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('PHONE_USER_REMOVE', JSON.stringify(null));
  navigation('/dash');
 }

 return (
  <button onClick={handlepath} className='Btn'>Anuller</button>
 );
};
export const DeleteBtn = () => {

 const [partnerAgent, setPartnerAgent] = React.useState();
 const [partnerClient, setPartnerClient] = React.useState();

 React.useEffect(async () => {

  const clientSnapshot = await getDocs(collection(db, "client"));
  clientSnapshot.forEach((doc) => { arrayPush.push(doc.id); });

  try {

   await onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setPartnerAgent(doc.data().agent);
    setPartnerClient(doc.data().client);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const isphoneInDocs = arrayPush.some(value => value == JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE')));

 const handleDelete = async (event) => {
  event.preventDefault();

  let valuePartnerAgent = Number(partnerAgent[nowField]) - 1;
  let valuePartnerClient = Number(partnerClient[nowField]) - 1;

  let arrayPartnerAgent = partnerAgent;
  let arrayPartnerClient = partnerClient;

  for (let i = nowField; i <= 31; i++) {
   arrayPartnerAgent[i] = valuePartnerAgent;
   arrayPartnerClient[i] = valuePartnerClient;
  }


  await deleteDoc(doc(db, isphoneInDocs ? "client" : "agent", JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE'))));
  window.setTimeout(() => {
   window.location.href = "/dash";
  }, 600);

  window.localStorage.setItem('PHONE_USER_REMOVE', JSON.stringify(null));
 }

 return (
  <button onClick={handleDelete} className='Btn-Red'>Supprimer</button>
 );
};
