import React from 'react';
import './Icon.css';
import { VscCheck } from 'react-icons/vsc';
import { TweenMax, Expo } from 'gsap';
import Media from 'react-media';

// Return Icon
export default function ReturnICon() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => {

 React.useEffect(() => {
  TweenMax.from('.wrp-success-icon', 2, { delay: 1.2, opacity: 0, ease: Expo.easeIn });
  TweenMax.from('.wrp-success-icon h1', 1.2, { delay: 2.2, opacity: 0, y: 20, ease: Expo.easeIn });
 }, [])

 return (
  <div className='wrp-success-icon'>
   <VscCheck size={'2em'} />
   <h1>Envoyer avec success</h1>
  </div>
 );
};
export const ScreenSmall = () => {

 React.useEffect(() => {
  TweenMax.from('.wrp-success-icon-sm', 2, { delay: 1.2, opacity: 0, ease: Expo.easeIn });
  TweenMax.from('.wrp-success-icon-sm h1', 1.2, { delay: 2.2, opacity: 0, y: 20, ease: Expo.easeIn });
 }, [])

 return (
  <div className='wrp-success-icon-sm'>
   <VscCheck size={'2em'} />
   <h1>Envoyer avec success</h1>
  </div>
 );
};
