import React from 'react';
import './Date.css';
import Media from 'react-media';
import { phoneX } from '../../../../Sign/Input';
import { collection, getDocs, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../../../firebase';
import moment from 'moment';
import 'moment/locale/fr';


// Return Date Component 
export default function ReturndAte() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 )
};

export const ScreenLarge = () => (
 <div className='wrp-actif-date'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-actif-date-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 let pushDocs = new Array();
 const [time, setTime] = React.useState();

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);

  });

  const verifierCollection = pushDocs.some(value => value == phoneX);
  const unsub = onSnapshot(doc(db, verifierCollection ? "client" : "agent", phoneX), (doc) => {
   setTime(doc.data().delay);
  });

 }, []);

 return (
  <>
   <h1>Date</h1>
   <p>{moment(time).locale('fr').format('LLL')}</p>
  </>
 );
};
