import React from 'react';
import './Input.css';
import REturnPriX from './Prix';
import Media from 'react-media';
import {
 useForm
} from 'react-hook-form';


// Return Phone input component
export default function REturnInputPhone() {
 return (
  <>
   <Media
    queries={{
     small: '(max-width: 599px)',
     medium: '(min-width: 600px) and (max-width:1199px)',
     large: '(min-width: 1200px)',
    }}>
    {matches => (
     <>
      {matches.small && <ScreenSmall />}
      {matches.medium && <ScreenLarge />}
      {matches.large && <ScreenLarge />}
     </>
    )}
   </Media>
  </>
 );
};
export const ScreenLarge = () => {

 const { register, handleSubmit, watch } = useForm();
 const onSubmit = data => {
  console.log(data);
 };

 const [count, setCout] = React.useState(null)

 let newCount = isNaN(watch('count'));
 newCount += count;
 console.log(newCount);

 return (
  <>
   <REturnPriX count={`${newCount}`} />
   <div className='wrp-inputphone-send'>
    <h2>Entre numero du beneficier</h2>
    <form>
     <input type={'text'} name="phone" />
     <input type={'text'} {...register('count')} name="count" placeholder={'0'} onFocus className='input-count' />
     <button className='btn'>Envoyer</button>
    </form>
   </div>
  </>
 );
};
export const ScreenSmall = () => {
 return (
  <>
   <REturnPriX count={0} />
   <div className='wrp-inputphone-send-sm'>
    <h2>Entre numero du beneficier</h2>

    <form>
     <input type={'text'} name="phone" />
     <input type={'text'} name="count" placeholder={'0.0'} onFocus className='input-count' />
     <button className='btn'>Envoyer</button>
    </form>

   </div>
  </>
 )
}
