import React from 'react';
import './InputPhone.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

// Input Field form
export default function REturnInPutCode() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='form-input-sign'>
  <FormDataInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-sign-sm'>
  <FormDataInput />
 </div>
);

export const FormDataInput = () => {

 const [checked, setChecked] = React.useState(true);
 const { register, handleSubmit, formState } = useForm({});
 const { isSubmitSuccessful } = formState;
 const navigation = useNavigate();

 const onsubmit = async data => {
  window.console.log(data);
  !!data && navigation('/register');
 };

 const handleChecked = (event) => {
  event.preventDefault();
  setChecked(false);
 }

 return (
  <form onSubmit={handleSubmit(onsubmit)}>
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   <button disabled={isSubmitSuccessful} className='Btn'>Connexion</button>
  </form>
 );
}
