import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { auth } from '../../firebase';
import {
 createUserWithEmailAndPassword,
 signInWithEmailAndPassword, onAuthStateChanged
} from "firebase/auth";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';
import { doc, updateDoc, increment, onSnapshot, arrayUnion, setDoc, collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';
import moment from 'moment';
import Media from 'react-media';

let now = moment().date();
let clientDocs = new Array();
let agentDocs = new Array();


export default function ReturnInPut() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
}

export const ScreenLarge = () => (
 <div className='input-sign-hm'>
  <VieW />
 </div>
);
export const ScreenSmall = () => (
 <div className='input-sign-hm'>
  <VieW />
 </div>
);

// Input form field 
export function VieW() {

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 const [open, setOpen] = React.useState(false);
 const [currentUser, setCurrentUser] = React.useState([]);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 // ================================================
 // Array admin emoney and  commission
 const [tableMuuCdfE, setTableMuuCdfE] = React.useState([]);
 const [tableMuuUsdE, setTableMuuUsdE] = React.useState([]);

 const [tableMuuCdfC, setTableMuuCdfC] = React.useState([]);
 const [tableMuuUsdC, setTableMuuUsdC] = React.useState([]);

 // Array partner
 const [tablepartnerA, setTablepartnerA] = React.useState([]);
 const [tablepartnerC, setTablepartnerC] = React.useState([]);

 // Array Agent emoney and  commission
 const [tableAgentCdfE, setTableAgentCdfE] = React.useState([]);
 const [tableAgentUsdE, setTableAgentUsdE] = React.useState([]);

 const [tableAgentCdfC, setTableAgentCdfC] = React.useState([]);
 const [tableAgentUsdC, setTableAgentUsdC] = React.useState([]);

 // Array Client emoney
 const [tableClientCdfE, setTableClientCdfE] = React.useState([]);
 const [tableClientUsdE, setTableClientUsdE] = React.useState([]);

 // Array Upgrade 
 const [tableUpCdfE, setTableUpCdfE] = React.useState([]);
 const [tableUpUsdE, setTableUpUsdE] = React.useState([]);

 React.useEffect(async () => {

  onAuthStateChanged(auth, user => { setCurrentUser(user) });

 }, [currentUser]);

 React.useEffect(async () => {

  try {

   await onSnapshot(doc(db, "muungano", "emoney"), (doc) => {
    setTableMuuCdfE(doc.data().cdf);
    setTableMuuUsdE(doc.data().usd);
   });
   await onSnapshot(doc(db, "muungano", "commission"), (doc) => {
    setTableMuuCdfC(doc.data().cdf);
    setTableMuuUsdC(doc.data().usd);
   });
   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgentCdfE(doc.data().cdf);
    setTableAgentUsdE(doc.data().usd);
   });
   await onSnapshot(doc(db, "agents", "commission"), (doc) => {
    setTableAgentCdfC(doc.data().cdf);
    setTableAgentUsdC(doc.data().usd);
   });
   await onSnapshot(doc(db, "clients", "emoney"), (doc) => {
    setTableClientCdfE(doc.data().cdf);
    setTableClientUsdE(doc.data().usd);
   });
   await onSnapshot(doc(db, "muungano", "upgrade"), (doc) => {
    setTableUpCdfE(doc.data().cdf);
    setTableUpUsdE(doc.data().usd);
   });
   await onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setTablepartnerA(doc.data().agent);
    setTablepartnerC(doc.data().client);
   });

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    clientDocs.push(doc.id);
   });

   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    agentDocs.push(doc.id);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);


 const handleClose = () => {
  setOpen(false);
 };

 // Ref collection database!
 const onSubmit = async (data) => {

  // await createUserWithEmailAndPassword(auth, data.email, data.password)
  //   .then((userCredential) => {
  //     // Signed in 
  //     const user = userCredential.user;
  //     console.log(user);
  //   })
  //   .catch((error) => {
  //     const errorCode = error.code;
  //     const errorMessage = error.message;
  //     window.console.log(errorCode + ' ' + errorMessage);
  //     setOpen(true);
  //     // window.alert(`L'accès n'est pas autorisé,e-mail ou mot de passe administrateur, n'est pas valide.`);
  //     reset();
  //   });


  await signInWithEmailAndPassword(auth, data.email, data.password)
   .then((userCredential) => {

    // Signed in 
    const user = userCredential.user;
    if (user.uid != null) {
     window.localStorage.setItem('ACTIVE', JSON.stringify(true));
     navigation('/dash');
    }

   })
   .catch((error) => {

    const errorCode = error.code;
    const errorMessage = error.message;
    window.console.log(errorCode + ' ' + errorMessage);
    setOpen(true);
    reset();
   });

  // This Solde Admin
  let soldeMuuCdfE = Number(Math.max(...tableMuuCdfE)); // get solde admin Max in the array
  let soldeMuuUsdE = Number(Math.max(...tableMuuUsdE)); // get solde admin Max in the array

  let soldeMuuCdfC = Number(Math.max(...tableMuuCdfC)); // get solde admin Max in the array
  let soldeMuuUsdC = Number(Math.max(...tableMuuUsdC)); // get solde admin Max in the array

  // This solde Agent
  let soldeAgentCdfE = Number(Math.max(...tableAgentCdfE)); // get solde admin Max in the array
  let soldeAgentUsdE = Number(Math.max(...tableAgentUsdE)); // get solde admin Max in the array

  let soldeAgentCdfC = Number(Math.max(...tableAgentCdfC)); // get solde admin Max in the array
  let soldeAgentUsdC = Number(Math.max(...tableAgentUsdC)); // get solde admin Max in the array

  // This solde Client
  let soldeClientCdfE = Number(Math.max(...tableClientCdfE)); // get solde admin Max in the array
  let soldeClientUsdE = Number(Math.max(...tableClientUsdE)); // get solde admin Max in the array

  // This solde Upgrade
  let soldeUpCdfE = Number(Math.max(...tableUpCdfE)); // get solde admin Max in the array
  let soldeUpUsdE = Number(Math.max(...tableUpUsdE)); // get solde admin Max in the array

  // This Partner Agent and Client
  let soldePartnerA = Number(Math.max(...tablepartnerA)); // get agent partner Max in the array
  let soldePartnerC = Number(Math.max(...tablepartnerC)); // get agent partner Max in the array

  // This Array Admin Emoney and Commission
  let arrayMuuCdfE = tableMuuCdfE;
  let arrayMuuUsdE = tableMuuUsdE;

  let arrayMuuCdfC = tableMuuCdfC;
  let arrayMuuUsdC = tableMuuUsdC;

  // This Array Agent Emoney and Commission
  let arrayAgentCdfE = tableAgentCdfE;
  let arrayAgentUsdE = tableAgentUsdE;

  let arrayAgentCdfC = tableAgentCdfC;
  let arrayAgentUsdC = tableAgentUsdC;

