import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

import { doc, setDoc, serverTimestamp, updateDoc, increment } from 'firebase/firestore';
import { db } from '../../firebase';
import { phoneX } from '../Phone/Input';


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => {
 return (
  <div className='wrp-input-signin-register'>
   <FormInput />

  </div>
 )
}
export const ScreenSmall = () => {
 return (
  <div className='wrp-input-signin-register-sm'>
   <FormInput />
  </div>
 )
};
export const FormInput = () => {

 let regular = /[a-z]+/;
 const navigation = useNavigate();
 const existing = { state: "client", cdf: 0, usd: 0, datethrift: '', verifierthrift: false, thrift: 0, accountdate: serverTimestamp(), delay: '', code: 'ungama', phoneclient: '', unite: '', money: 0, frais: 0, solde: 0, user: '' }

 const { register, handleSubmit } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });

 // Ref collection database!
 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   window.alert('Numero de t√©lphone Incorrect!');
  } else {

   const clone = Object.assign(data, existing);
   addUserInWithDocs(phoneX, clone);
   countUserInWithDocs();
   navigation('/register/success');
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='Nom' name='firstname' {...register('firstname', { required: true })} />
   <input type='text' placeholder='Pr√®nom' name='lastname' {...register('lastname', { required: true })} />
   <input type='email' placeholder='Email' name='email' {...register('email', { required: false })} />
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
   <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />
   <input type='date' placeholder='date de naissance' name='birthey' {...register('birthey', { required: true })} />
   <button className='Btn'>Enregistre</button>
  </form>
 )
};
// User Component Register
async function addUserInWithDocs(numPhone, datA) {
 const cityRef = doc(db, 'client', numPhone);
 setDoc(cityRef, datA);
};
async function countUserInWithDocs() {
 const washingtonRef = doc(db, "muunganomoney", "partner");
 await updateDoc(washingtonRef, {
  client: increment(1),
 });
};
