import React from 'react';
import './Char.css';
import { db } from '../../firebase';

// Phone Component 
export default function RetuRnChArs() {

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });


  let datalastphone = window.localStorage.getItem('PHONE_USER_TRAFIK');
  // setLastPhone(JSON.parse(datalastphone))

  const verifier = pushArray.some(value => value == JSON.parse(datalastphone));
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", JSON.parse(datalastphone)), (doc) => {
   setFirst(doc.data().firstname);
   setLast(doc.data().lastname);
   setState(doc.data().state);
   setAdresse(doc.data().addresse);
   setPhone(doc.data().phone);
   setPassword(doc.data().code);
   setBirth(doc.data().birth);
   setAccountdate(doc.data().accountdate);
  });

 }, []);

 return (
  <div className='char-user-client'><h1>J</h1></div>
 );
};
