import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { updatePassword } from "firebase/auth";
import { auth } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import ReturnQuote from './Quote';
import ReturnQuOte from './Quote';
import REturnLogo from './Logo';

import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';


import { collection, getDocs, doc, updateDoc } from 'firebase/firestore';
import { db } from '../firebase';

import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='form-input-auth'>
  <ReturnQuote />
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-auth'>
  <ReturnQuote />
  <FormInput />
 </div>
);
export const FormInput = () => {

 const user = auth.currentUser;
 const navigation = useNavigate();
 const { register, handleSubmit, reset, control, watch } = useForm({});

 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const [firstChart, setFirstChart] = React.useState(false);
 const [checked, setChecked] = React.useState(false);
 const [showPassword, setShowPassword] = React.useState(false);


 const handleClickShowPassword = () => setShowPassword((show) => !show);
 const handleMouseDownPassword = (event) => {
  event.preventDefault();
 };


 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

 }, []);
 React.useEffect(() => {

  const subscription = watch((data) => {

   if (/^\d+$/.test(`${data.first}`) && data.first.length <= 6) {
    setFirstChart(false);
   } else {
    setFirstChart(true);
   }

   if (data.first != data.second) {
    setChecked(true);
   } else {
    setChecked(false);
   }

  });

  return () => {
   subscription.unsubscribe();
  }

 }, [watch]);


 const onSubmit = async (data) => {

  if (data.first != data.second) {

   setOpen(true);
   reset();

  } else {

   updatePassword(user, data.pwd).then(() => {
    // Update successful.
    window.localStorage.setItem('?bu%^%ild--#', JSON.stringify(false));
    navigation('/');

   }).catch((error) => {
    // An error ocurred
    setOpen(true);
    window.console.log(error);
   });

  };


  reset();
 };

 return (
  <form
   autoComplete='off'
   onSubmit={handleSubmit(onSubmit)}>

   <Box sx={{ display: 'flex', flexWrap: 'wrap' }}>

    <FormControl
     sx={{ width: '100%' }}

     variant="standard">
     <InputLabel htmlFor="standard-adornment-password">Nouveau code</InputLabel>

     <Controller
      name="first"
      control={control}
      render={({ field }) =>

       <Input
        id="standard-adornment-password"
        autocomplete="new-password"
        {...field}
        type={showPassword ? 'text' : 'password'}

        endAdornment={
         <InputAdornment position="end">

          <IconButton
           aria-label="toggle password visibility"
           onClick={handleClickShowPassword}
           onMouseDown={handleMouseDownPassword}
          >
           {showPassword ? <VisibilityOff /> : <Visibility />}
          </IconButton>

         </InputAdornment>
        }
       />}
     />

    </FormControl>

    {firstChart && <p className='wrp-errors-code'>Seul caractère numérique, a 6 chiffre sont valides</p>}
    <p className='wrp-errors-code-transparent'>Seul caractère numérique</p>
   </Box>

   <Box sx={{ display: 'flex', flexWrap: 'wrap' }}>

    <FormControl
     sx={{ width: '100%' }}

     variant="standard">
     <InputLabel htmlFor="standard-adornment-password">Confirmer le nouveau code</InputLabel>

     <Controller
      name="second"
      control={control}
      render={({ field }) =>

       <Input
        id="standard-adornment-password"
        {...field}
        type={showPassword ? 'text' : 'password'}

        endAdornment={
         <InputAdornment position="end">

          <IconButton
           aria-label="toggle password visibility"
           onClick={handleClickShowPassword}
           onMouseDown={handleMouseDownPassword}
          >
           {showPassword ? <VisibilityOff /> : <Visibility />}
          </IconButton>

         </InputAdornment>
        }
       />}
     />

    </FormControl>

    {checked && <p className='wrp-errors-code'>Les codes sont différents vérifier</p>}
    <p className='wrp-errors-code-transparent'>Seul caractère numérique</p>
   </Box>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}>

    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le code saisi est valide avec six chiffres numériques.
      Évitez les valeurs identiques en caractères.
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <button className='Btn'>Valider</button>
  </form>
 )
};
