import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { db } from '../../firebase';


// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const onSubmit = async (data) => {

  if (verifierCodeAccessUngame() == data.code) {
   updateSolde(data.count);
   addSoldeAgent(phoneX, data.count);
  } else {
   window.alert(`Erreur Code Invalid.`);
  };

  window.console.log(data);
  navigation('/send/success');
  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'Fc'}
   />
   <input type={'text'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};

// Verifier code access view
export const verifierCodeAccessUngame = async () => {

 const docRef = doc(db, "ungama", "money");
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  return docSnap.data().code;
 }

};
// get money pyramid
export const updateSolde = async (solde) => {

 let getFc = 0;
 const docRef = doc(db, "ungama", 'money');
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  getFc = docSnap.data().fc
 }

 getFc = Number(getFc) - Number(solde);
 const washingtonRef = doc(db, "ungama", "money");

 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  fc: Math.floor(getFc)
 });

};
// get add some money on the agent
export const addSoldeAgent = async (numPhoneInDocs, solde) => {

 let getFc = 0

 const docRef = doc(db, "agent", numPhoneInDocs);
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  getFc = docSnap.data().fc;
  getFc = Number(getFc) + Number(solde);
 }

 const washingtonRef = doc(db, "agent", numPhoneInDocs);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  fc: Math.floor(getFc)
 });

};
