import './BtnCurrent.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';

// Button Current Component
export default function ReturnButtonCuRRent() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btn-current-account'>
  <ViewText />
  <HanDleNavigation />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-current-account-sm'>
  <ViewText />
  <HanDleNavigation />
 </div>
);
export const HanDleNavigation = () => {
 return (
  <ViewButton />
 )
};

const ViewText = () => (
 <p>
  Commencer à accepter la crypto sur votre site Web en quelques étapes, tous les paiements via BTC, seront convertis en USD, FC (pas de risques avec les taux) et déposés sur votre compte UngamaMoney,
  utiliser instantanément le solde pour les paiements cryptographiques.
 </p>
);
const ViewButton = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/current-money');
 };

 return (
  <button onClick={handlepath} className='Btn'>Utiliser Compte Current</button>
 )
};
