import './Icon.css';
import Media from 'react-media';

// Icon Card view 
export default function ReturnIconCard(props) {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall icon={props.icon} Text={props.Text} />}
     {matches.medium && <ScreenLarge icon={props.icon} Text={props.Text} />}
     {matches.large && <ScreenLarge icon={props.icon} Text={props.Text} />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = (props) => (
 <div className='wrp-icon-card'>
  <span>{props.icon}</span>
  <p>{props.Text}</p>
 </div>
);
export const ScreenSmall = (props) => (
 <div className='wrp-icon-card-sm'>
  <span>{props.icon}</span>
  <p>{props.Text}</p>
 </div>
);
