import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';


// Firebase auth 
export let phoneX = '';



// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-phone-register'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-phone-register-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let regular = /[a-z]+/;
 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm({ defaultValues: { phone: '+243' } });

 const onSubmit = async (data) => {

  if (data.phone.length != 13) {
   window.alert('Numero Invalid.');
  } else {

   if (regular.test(data.phone)) {
    window.alert('Erreur numero invalid.');
   } else {

    if (searchDocsInWithFirebase(data.phone)) {
     phoneX = data.phone
     navigation('/send-withdraw');
    } else {
     window.alert(`Numero n'exists pas`);
     reset();
     // navigation('/dash');
    }

    // phoneX = data.phone;
    // navigation('/send-withdraw');
   }
  };

  window.console.log(data);
  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text'
    placeholder='Phone'
    name='phone' {...register('phone', { required: true })}
   />
   <button className='Btn'>Continue</button>
  </form>
 )
};

async function searchDocsInWithFirebase(phoneDocs) {

 let pushDocs = new Array();
 const querySnapshot = await getDocs(collection(db, "agent"));
 querySnapshot.forEach((doc) => {
  pushDocs.push(doc.id);
 });

 const checkedNumber = pushDocs.some(value => value == phoneDocs);
 return checkedNumber;
};
