import React from 'react';
import './Basic.css';
import ReturnHeAd from './Head';
import ReturnText from './Text';
import ReturnInFo from './Info';
import ReturnConfidentiAl from './Confidential';
import { collection, getDocs, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';

// Return Box 
export default function BasicInFo() {

 let pushDocs = new Array();
 const [num, setNum] = React.useState(null);
 const [mail, setMail] = React.useState(null);
 const [first, setFirst] = React.useState(null);
 const [last, setLast] = React.useState(null);
 const [city, setCity] = React.useState(null);
 const [status, setStatus] = React.useState(null);
 const [birthay, setBirthay] = React.useState(null);
 const [franc, setFranc] = React.useState(null);
 const [dollar, setDollar] = React.useState(null);
 const [password, setPassword] = React.useState(null);
 const [login, setLogin] = React.useState(null);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  const collections = pushDocs.some(value => value == JSON.parse(window.localStorage.getItem('%^info&*ºº––')));
  const unsub = onSnapshot(doc(db, collections ? "client" : "agent", JSON.parse(window.localStorage.getItem('%^info&*ºº––'))), (doc) => {
   setNum(doc.data().phone);
   setMail(doc.data().email);
   setFirst(doc.data().firstname);
   setLast(doc.data().lastname);
   setCity(doc.data().addresse);
   setStatus(doc.data().state);
   setBirthay(doc.data().birth);
   setFranc(doc.data().cdf);
   setDollar(doc.data().usd);
   setPassword(doc.data().pin);
   setLogin(doc.data().code);
  });

 }, []);

 return (
  <div className='wrp-box-flex-text'>


   <div className='basic-info'>
    <ReturnHeAd Text='information contact' />

    <div className='info-flex-gap'>
     <ReturnText subject='Phone' Info={num} />
     <ReturnText subject='Email' Info={mail} />
     <ReturnText subject='Addresse' Info={city} />
    </div>

   </div>

   <div className='basic-info'>
    <ReturnHeAd Text='basic information' />

    <div className='info-flex-gap'>
     <ReturnInFo subject='Nom' Info={first} />
     <ReturnInFo subject='Prénom' Info={last} />
     <ReturnInFo subject='Status' Info={status} />
     <ReturnInFo subject='Date de naissance' Info={birthay} />
    </div>

   </div>

   <div className='basic-info'>
    <ReturnHeAd Text='information confidential' />

    <div className='info-flex-gap'>
     <ReturnInFo subject='CDF' Info={franc} />
     <ReturnInFo subject='USD' Info={dollar} />
     <ReturnInFo subject='mot de passe' Info={status} />
     <ReturnInFo subject='mot de passe de connexion' Info={status} />
    </div>

   </div>


  </div>
 );
};
