import React from 'react';
import './Char.css';
import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';

let pushArray = new Array();

// Phone Component 
export default function RetuRnChArs() {

 const [first, setFirst] = React.useState('M');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });

  const verifier = pushArray.some(value => value == JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE')));
  onSnapshot(doc(db, verifier ? "client" : "agent", JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE'))), (doc) => {
   setFirst(doc.data().firstname);
  }).then(() => {
   window.console.log('Success remove')
  }).catch(() => {
   window.console.log('Error remove')
  });

 }, []);

 let user = `${first}`;

 return (
  <div className='char-user-client'><h1>{user[0].toUpperCase()}</h1></div>
 );
};
