import React from 'react';
import './Input.css';
import REturnPriX from './Prix';
import Media from 'react-media';
import {
 useForm
} from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { Usd } from '../Money';

import { doc, getDocs, collection, getDoc } from 'firebase/firestore';
import { db } from '../firebase';
import { phoneX } from '../Sign/Input';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';


export let money = 0;
export let count = 0;
export let frais = 0;
export let solde = 0;
export let Unite = null;


// Return Phone input component
export default function REturnInputPhone() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => {
 return (
  <FormInputValue />
 );
};
export const ScreenSmall = () => {
 return (
  <FormInputValue />
 )
};
export const FormInputValue = () => {

 const navigation = useNavigate();
 const { register, handleSubmit, watch, reset } = useForm();

 let verifierCollection = null;
 let pushUser = new Array();

 const [prix, setPrix] = React.useState(Usd);
 const [agent, setAgent] = React.useState();

 let entry = watch('count');
 let value = entry == undefined ? prix : Number(prix) - Number(entry);


 const [checked, setChecked] = React.useState(true);
 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };


 if (Number(entry) <= 5) {
  frais = remainderInput(entry, 17.5);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 6 && Number(entry) <= 10) {
  frais = remainderInput(entry, 8.7);
  count = count = doesInputEntry(entry, frais)
 }
 else if (Number(entry) >= 11 && Number(entry) <= 20) {
  frais = remainderInput(entry, 5.4);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 21 && Number(entry) <= 30) {
  frais = remainderInput(entry, 2.8);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 31 && Number(entry) <= 50) {
  frais = remainderInput(entry, 2.9);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 51 && Number(entry) <= 100) {
  frais = remainderInput(entry, 1.98);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 101 && Number(entry) <= 400) {
  frais = remainderInput(entry, 1.7);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 401 && Number(entry) <= 1000) {
  frais = remainderInput(entry, 1.69);
  count = doesInputEntry(entry, frais);
 }
 else if (Number(entry) >= 1001 && Number(entry) <= 2000) {
  frais = remainderInput(entry, 1.6);
  count = doesInputEntry(entry, frais);
 }
 else {
  frais = remainderInput(entry, 1.6);
  count = doesInputEntry(entry, frais)
 };


 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushUser.push(doc.id);
  });
  verifierCollection = pushUser.some(value => value == phoneX);

  const docRef = doc(db, verifierCollection ? "client" : "agent", phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setPrix(docSnap.data().usd);
   setAgent(docSnap.data().state);
  }

 }, []);

 const onSubmit = async (data) => {

  if (Number(data.count) > Number(prix) || Number(data.count) < 1) {
   setOpen(true);
   reset();
  } else {

   money = Math.floor(Number(count));
   Unite = 'USD'
   frais = Number(frais);
   solde = Math.floor(Number(data.count));
   navigation('/valid-usd');
  }
 };

 return (
  <>
   <REturnPriX count={`${value}`} />
   {agent != "agent" && <div className='wrp-frais-withdraw'>{`Frais ${count}`}</div>}

   <div className='wrp-inputphone-send'>
    <h2>Entre votre Money</h2>
    <form onSubmit={handleSubmit(onSubmit)}>

     <input type={'number'}
      {...register('count', { required: true })}
      name="count"
      placeholder={'0'}
      className='input-count'
     />

     <Dialog
      fullWidth={fullWidth}
      maxWidth={maxWidth}
      open={open}
      onClose={handleClose}
     >
      <DialogTitle>MuunganoMoney</DialogTitle>
      <DialogContent>

       <DialogContentText>
        La valeur demandée n'est pas disponible, veuillez vérifier.
     </DialogContentText>

      </DialogContent>
      <DialogActions>
       <Button onClick={handleClose}>Fermer</Button>
      </DialogActions>
     </Dialog>

     <button className='Btn'>Suivant</button>

    </form>
   </div>
  </>
 );
};

// ======================================
export const doesInputEntry = (x, y) => {
 return Math.floor(Number(x) - Number(y));
};
export const remainderInput = (entryInput, value) => {

 let number = (entryInput * value) / 100;
 let numberRound = Math.round(number * 100) / 100;

 return numberRound;

};
