import React from 'react';
import './InputPhone.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';

// Input Field form
export default function REturnInPutCode() {

 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => {

 const { register, handleSubmit } = useForm();
 const onsubmit = (data) => {
  window.alert(data);
  console.log(data);
 };

 return (
  <div className='form-input-sign'>
   <form onSubmit={handleSubmit(onsubmit)}>
    <input type='phone' focus placeholder='Phone' name='phone'  {...register('phone', { required: true })} />
    <button className='Btn'>Connexion</button>
   </form>
  </div>
 )
};


export const ScreenSmall = () => {
 return (
  <div className='form-input-sign-sm'>
   <FormDataInput />
  </div>
 );
};


export const FormDataInput = () => {

 const { register, handleSubmit } = useForm();
 const onsubmit = (data) => {
  window.alert(data);
 };

 return (
  <form onSubmit={handleSubmit(onsubmit)}>
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   <button className='Btn'>Connexion</button>
  </form>
 );
}
