import React from 'react';
import './Quote.css';
import Media from 'react-media';
import { phoneX } from '../Sign/Input';
import { sendPhone } from '../ValidExUsd/Input';

import { doc, getDoc, collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';

// View Quote Component 
export default function ReturnQuote() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-quote-withdraw-success'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-quote-withdraw-success-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 let pushDocs = new Array();
 const [stateX, setStateX] = React.useState(null);
 const [stateY, setStateY] = React.useState(null);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);

  });

  const verifierX = pushDocs.some(value => value == phoneX);
  const verifierY = pushDocs.some(value => value == sendPhone);

  const docRef = doc(db, verifierX ? "client" : "agent", phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setStateX(docSnap.data().state);
  };

  const docRefY = doc(db, verifierY ? "client" : "agent", sendPhone);
  const docSnapY = await getDoc(docRefY);

  if (docSnapY.exists()) {
   setStateY(docSnapY.data().state);
  };


 }, []);

 return (
  <>
   {stateX == "agent" && stateY == "agent"
    ?
    <p></p>
    :
    <p>Envoyer des E-money au-delà des frontières est aussi simple que de les envoyer de l'autre côté de la rue.</p>
   }
  </>
 );
};
