import React from 'react';
import './Pin.css';
import { IoIosCodeWorking, IoMdCreate } from 'react-icons/io';
import Media from 'react-media';
import ReturnFormUpdate from './Input';

// This is Name view 
export default function ReturnPinSetting() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-name-pin'>
  <div className='wrp-boxname-pin'>
   <IoIosCodeWorking size={'20px'} color={'grey'} />
   <FormInput />
  </div>
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-name-pin-sm'>
  <div className='wrp-boxname-pin-sm'>
   <IoIosCodeWorking size={'2.5em'} color={'grey'} />
   <FormInput />
  </div>
 </div>
);
export const FormInput = () => {

 const [checked, setChecked] = React.useState(true);
 const handleChecked = (event) => {
  event.preventDefault();
  setChecked(false);
 };
 return (
  <>
   {checked ?
    <>
     <div className='pin-name-identity-sm'>
      <h1>Modifier (PIN)</h1>
      <h2>{'* * * * * * * * *'}</h2>
      <ViewText/>
     </div>

     <div style={{ cursor: 'pointer' }} onClick={handleChecked}>
      <ViewIoMdsmAll />
     </div>
    </>
    :
    <ReturnFormUpdate />
   }
  </>
 );
};

export const  ViewText = () => (
<p>Modifier code (Pin) d'un compte MuunganoMoney.</p>
);
export const ViewIoMdsmAll = () => (
 <IoMdCreate size={'20px'} color={'#ffd600'} />
);
