import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, updateDoc, increment, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';

import { phoneX } from '../../../Sign/Input';
import { sendPhone } from '../../../ValidExUsd/Input';
import { frais, Unite, solde, money } from '../../../USD/Input';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import moment from 'moment';


export let prixHash = 0;
export let hashTrue = true;

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => (
 <div className='wrp-input-phone-register'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-phone-register-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 // send React useState  
 const [pin, setPin] = React.useState(null);
 const [state, setState] = React.useState(null);
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 // getting React useState
 const [getstate, setGetstate] = React.useState(null);
 const [getfirst, setGetFirst] = React.useState('');
 const [getlast, setGetLast] = React.useState('');


 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  verifierSend = pushDocs.some(value => value == phoneX);
  verifierGet = pushDocs.some(value => value == sendPhone);

  const sendunsub = onSnapshot(doc(db, verifierSend ? "client" : "agent", phoneX), (doc) => {
   setPin(doc.data().code);
   setState(doc.data().state);
   setFirst(doc.data().firstname);
   setLast(doc.data().lastname);
  });
  const getunsub = onSnapshot(doc(db, verifierGet ? "client" : "agent", sendPhone), (doc) => {
   setGetstate(doc.data().state);
   setGetFirst(doc.data().firstname);
   setGetLast(doc.data().lastname);
  });

 }, []);

 const onSubmit = async (data) => {

  // Checked if value code is length valid
  if (data.code.length != 6 || pin != data.code) {
   setOpen(true);
   reset();

  } else {
   const sendUser = first.toLowerCase() + ' ' + last.toLowerCase();
   const getUser = getfirst.toLowerCase() + ' ' + getlast.toLowerCase();

   if (state == "agent" && getstate == "agent") {

    hashTrue = false;
    prixHash = solde;
    swapInWithDocsAgent(phoneX, sendPhone, sendUser, getUser, solde, Unite);
    navigation('/send-success-usd');

   } else if (state == "client" && getstate == "client") {

    hashTrue = true;
    prixHash = money;
    isSwapInWithClientToClient(phoneX, sendPhone, sendUser, getUser, solde, money, Unite);
    navigation('/send-success-usd');

   } else {

    if (state == "agent") {

     hashTrue = false;
     prixHash = solde;
     console.log('agent');
     swapInWithDocsAgentToClient(phoneX, sendPhone, sendUser, getUser, solde, Unite);
     navigation('/send-success-usd');

    } else {

     hashTrue = true;
     prixHash = money;

     isSwapInWithClientToAgent(phoneX, sendPhone, sendUser, getUser, solde, money, Unite);

     console.log('other');
     navigation('/send-success-usd');

    }

   }

  }
 };


 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='password'
    placeholder='Code'
    name='code' {...register('code', { required: true })}
   />

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Code d'erreur (PIN) Accès non valide
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>


   <button className='Btn'>Valider</button>
  </form>
 )
};

async function swapInWithDocsAgent(sendPhone, getPhone, sendUser, getUser, prix, unite) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: prix,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  usd: increment(prix),
  money: prix,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

};
async function swapInWithDocsAgentToClient(sendPhone, getPhone, sendUser, getUser, prix, unite) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: prix,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  usd: increment(prix),
  money: prix,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

};
async function isSwapInWithClientToAgent(sendPhone, getPhone, sendUser, getUser, prix, money, unite) {

 const sendRef = doc(db, "client", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  usd: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  thriftusd: increment((Number(frais * 30 / 100))),
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  usd: increment((Number(frais * 70 / 100)))
 });

};
async function isSwapInWithClientToClient(sendPhone, getPhone, sendUser, getUser, prix, money, unite) {

 const sendRef = doc(db, "client", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  usd: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  usd: increment(Math.floor(Number(frais)))
 });

};
