import React from 'react';
import './Avatar.css';
// import { IoIosPerson } from 'react-icons/io';
import Media from 'react-media';
import ReturnName from './Name';

let firstLetter = '';

// Avatar IMAGE VIew
export default function ReturnAvataR() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-avatar-profil-dash'>
  <div className='avatar-circle-profil-dash'>
   <h1>{firstLetter}</h1>
  </div>
  <NameView />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-avatar-profil-dash-sm'>
  <div className='avatar-circle-profil-dash-sm'>
   <h1>{'L'}</h1>
  </div>

  <NameView />
 </div>
);
export const NameView = () => {

 const [first, setFirst] = React.useState('Tshilela');
 const [last, setLast] = React.useState('Miabial');

 firstLetter = first[0];
 return (
  <ReturnName firstName={first} lastName={last} />
 );
}
