import React, { useEffect, useRef } from 'react';

// Picture Main Return 
export default function ReturnPictuRe() {

 const videoRef = React.useRef(null);
 const photoRef = React.useRef(null);

 const getUserCamera = () => {
  navigator.mediaDevices.getUserMedia({
   video: true,
  }).then((strem) => {

   let video = videoRef.current;
   video.srcObject = strem;
   video.play()

  }).catch((error) => console.log('New current' + error));
 }
 const takePicture = () => {
  // width and height 
  let width = 500;
  let height = width / (13 / 10);

  let photo = photoRef.current;
  let video = videoRef.current;


  // set the photo width  and height
  photo.width = width;
  photo.height = height;

  let ctx = photo.getContext('2d');
  ctx.drawImage(video, 0, 0, photo.width, photo.height);
 }
 const clearImage = () => {

  let photo = photoRef.current;
  let ctx = photo.getContext('2d');
  ctx.clearRect(0, 0, photo.width, photo.height);

 };

 React.useEffect(() => {
  getUserCamera();
 }, [videoRef]);

 return (
  <div className='wrp-body-picture'>
   <h1>CAMERA ID</h1>
   <video className='wrp-video' ref={videoRef}></video>

   <div className='wrp-btn-photo'>
    <div onClick={takePicture} className='wrp-camera'>
     <i class="bi bi-camera-fill"></i>
    </div>

    <div onClick={clearImage} className='wrp-save'>
     <i class="bi bi-save"></i>
    </div>
   </div>


   <canvas ref={photoRef}></canvas>
  </div>
 )
};
